import { useRouter } from 'next/router'
import ErrorPage from 'next/error'
import { getSketchById, getSketchesIds, pickRandomSketch } from '../../lib/sketches';

export async function getStaticProps({ params }) {
    const sketches = sketches;
    const sketch = getSketchById(sketches, params.id);
    const randomSketch = pickRandomSketch(sketches, params.id);
    // const content = await markdownToHtml(post.content || ''

    return {
        props: {
            sketch,
            randomSketch
        },
    }
}

export async function getStaticPaths() {
    const sketches = getSketchesIds();

    return {
        paths: sketches.map((sketch) => {
            return {
                params: {
                    id: sketch,
                },
            }
        }),
        fallback: false,
    }
}

const Sketch = ({ sketch }) => {
    const router = useRouter()
    const { id } = router.query

    if (!router.isFallback && !sketch?.id) {
        return <ErrorPage statusCode={404} />   
    }

    return <p>Sketch: {id} - {sketch.title}</p>
}

export default Sketch
