import { useRouter } from 'next/router'
import ErrorPage from 'next/error'
import { getSketchById, getSketchesIds, pickRandomSketch, getSketches } from '../../lib/sketches';
import Sketch from '../../components/sketch';

export async function getStaticProps({ params }) {
    const sketches = getSketchesIds();
    const sketch = getSketchById(params.id);
    const randomSketch = pickRandomSketch(sketches, params.id);
    // const content = await markdownToHtml(post.content || ''
    console.log(sketch);
    return {
        props: {
            sketch,
            randomSketch
        },
    }
}

export async function getStaticPaths() {
    const sketches = getSketchesIds();

    return {
        paths: sketches.map((sketch) => {
            return {
                params: {
                    id: sketch,
                },
            }
        }),
        fallback: false,
    }
}

export default (props = {}) => {
    const router = useRouter()
    const { id } = router.query

    if (!router.isFallback && !props.sketch?.id) {
        return <ErrorPage statusCode={404} />
    }

    return <Sketch {...props} />
}
