import fs from 'fs';
import path from 'path';

const sketchsDir = path.join(process.cwd(), 'assets', 'projects');

export function getSketches() {
	const sketches = fs.readFileSync(path.join(sketchsDir, 'sketches.json'), { encoding: 'utf8' });

	return {
		params: {
			sketches
		}
	};
};
