import fs from 'fs';
import path from 'path';

const sketchsDir = path.join(process.cwd(), 'public', 'projects');

export function getSketches() {
	const sketches = fs.readFileSync(path.join(sketchsDir, 'sketches.json'), { encoding: 'utf8' });

	return JSON.parse(sketches);
};


export function getSketchById(id) {
	const sketches = getSketches();

	return sketches.filter((sketch) => sketch.id === id)[0];
};
