import fs from 'fs';
import path from 'path';

const sketchsDir = path.join(process.cwd(), 'assets', 'projects');

export function getSketches() {
	const sketches = fs.readFileSync(path.join(sketchsDir, 'sketches.json'));

	return {
		params: {
			sketches
		}
	};
};
