import { useRouter } from 'next/router'
import ErrorPage from 'next/error'
import { getSketchById, getSketchesIds } from '../../lib/sketches';

export async function getStaticProps({ params }) {
    const sketch = getSketchById(params.id)
    // const content = await markdownToHtml(post.content || ''

    return {
        props: {
            sketch
        },
    }
}

export async function getStaticPaths() {
    const sketches = getSketchesIds();

    return {
        paths: sketches.map((sketch) => {
            return {
                params: {
                    id: sketch,
                },
            }
        }),
        fallback: false,
    }
}

const Sketch = ({ sketch }) => {
    const router = useRouter()
    const { id } = router.query

    if (!router.isFallback && !sketch?.id) {
        return <ErrorPage statusCode={404} />   
    }

    console.log(sketch);

    return <p>Sketch: {id} - {sketch.name}</p>
}

export default Sketch
