import { useRouter } from 'next/router'
import { getSketches, getSketchById } from '../lib/sketches';

export async function getStaticProps() {
    const sketches = getSketches()

    return {
        props: { sketches },
    }
}

const Sketch = ({ sketches }) => {
    const router = useRouter()
    const { slug } = router.query
    const sketch = getSketchById(sketchs, slug)

    return <p>Sketch: {slug} - {sketch.name}</p>
}

export default Sketch
