import { useRouter } from 'next/router';
import Link from 'next/link'

export function ActiveLink({ children, href, as = href }) {
	const router = useRouter();
	const style = {

	};

	const handleClick = (e) => {
		e.preventDefault();
		router.push(href);
	};

	return (
		<Link href={href} as={as} >
			<a className="link" onClick={handleClick} style={style}>
				{children}
			</a>
		</Link>
	);
}
