import React from 'react';

const getBannerPath = (sketch, id) => (sketch.media.filter((media) => media.id === id)[0].path);

export const Banner = ({ sketch, assetsPath }) => (
    <div className="w-full mb-24" style={{ backgroundColor: sketch.banner.color }}>
        <div className="mx-auto md:h-64 h-48 overflow-hidden " style={{
            backgroundImage: `url(${assetsPath}${getBannerPath(sketch, sketch.banner.id)})`,
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'center',
            backgroundPositionY: '40%',
            backgroundSize: 'cover',
        }} />
    </div>
);
