import fs from 'fs';
import path from 'path';
import matter from 'gray-matter';
import remark from 'remark';
import html from 'remark-html';

const sketchsDir = path.join(process.cwd(), 'assets', 'projects', 'sketches');

export function getSketches() {
	const sketches = fs.readFileSync(path.join(sketchsDir, 'sketches.json'));

	return {
		params: {
			sketches
		}
	};
};
