import { useRouter } from 'next/router'
import ErrorPage from 'next/error'
import { getSketches, getSketchById } from '../../lib/sketches';

export async function getStaticProps({ params }) {
    const post = getSketchById(params.slug)
    // const content = await markdownToHtml(post.content || ''
    console.log(params);
    return {
        props: {
            sketch: {
                ...post,
            },
        },
    }
}

export async function getStaticPaths() {
    const sketches = getSketches();

    return {
        paths: sketches.map((sketch) => {
            return {
                params: {
                    slug: sketch.id,
                },
            }
        }),
        fallback: false,
    }
}

const Sketch = ({ sketch }) => {
    const router = useRouter()
    const { slug } = router.query

    if (!router.isFallback && !sketch?.slug) {
        return <ErrorPage statusCode={404} />   
    }

    console.log(sketch);

    return <p>Sketch: {slug} - {sketch.name}</p>
}

export default Sketch
