import React from 'react';
import { useRoute, Link } from 'wouter';

export const ActiveLink = props => {
	const [ isActive ] = useRoute(props.href);
	return (
		<Link {...props}>
			<a className={`${props.className || ''} link ${isActive ? ' active' : ''}`}>{props.children}</a>
		</Link>
	);
};
