import { useRouter } from 'next/router';

export function ActiveLink({ children, href }) {
	const router = useRouter();
	const style = {

	};

	const handleClick = (e) => {
		e.preventDefault();
		router.push(href);
	};

	return (
		<a className="link" href={href} onClick={handleClick} style={style}>
			{children}
		</a>
	);
}
